function abreFechaRespstas(t){t.target.parentNode.classList.toggle("faq__questions__item--is-open")}function removeBotao(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].classList.remove("shows__tabs__button--is-active")}function removeAba(){var e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.remove("shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll("[data-tab-button]"),a=document.querySelectorAll("[data-faq-question]");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){var e=t.target.dataset.tabButton,e=document.querySelector(`[data-tab-id=${e}]`);removeAba(),e.classList.add("shows__list--is-active"),removeBotao(),t.target.classList.add("shows__tabs__button--is-active")});for(let t=0;t<a.length;t++)a[t].addEventListener("click",abreFechaRespstas)});